import React from 'react';

const AttendeeForm = ({ attendeeData, setAttendeeData, events, handleSubmit }) => {
  return (
    <form onSubmit={handleSubmit}>
      <input
        type="text"
        placeholder="Attendee Name"
        value={attendeeData.name}
        onChange={(e) => setAttendeeData({ ...attendeeData, name: e.target.value })}
        required
      />
      <input
        type="email"
        placeholder="Attendee Email"
        value={attendeeData.email}
        onChange={(e) => setAttendeeData({ ...attendeeData, email: e.target.value })}
        required
      />
      <select
        value={attendeeData.eventId}
        onChange={(e) => setAttendeeData({ ...attendeeData, eventId: e.target.value })}
        required
      >
        <option value="">Select Event</option>
        {events.map((event) => (
          <option key={event.id} value={event.id}>{event.name}</option>
        ))}
      </select>
      <button type="submit">Add Attendee</button>
    </form>
  );
};

export default AttendeeForm;
