import React, { useState, useEffect } from 'react';
import axios from 'axios';
import "./../assets/styles.css";
const AttendeeManagementPage = () => {
  const [attendees, setAttendees] = useState([]);
  const [events, setEvents] = useState([]);
  const [attendeeData, setAttendeeData] = useState({ name: '', email: '', eventId: '' });

  // Fetch the list of attendees and events from backend
  useEffect(() => {
    axios.get('http://localhost:8080/api/attendees')
      .then(response => {
        setAttendees(response.data);
      })
      .catch(error => console.error('Error fetching attendees:', error));

    axios.get('http://localhost:8080/api/events')
      .then(response => {
        setEvents(response.data);
      })
      .catch(error => console.error('Error fetching events:', error));
  }, []);

  // Add a new attendee
  const handleSubmit = (e) => {
    e.preventDefault();

    axios.post('http://localhost:8080/api/attendees', attendeeData)
      .then(response => {
        setAttendees([...attendees, response.data]);
        setAttendeeData({ name: '', email: '', eventId: '' });
      })
      .catch(error => console.error('Error adding attendee:', error));
  };

  // Remove an attendee
  const handleRemove = (id) => {
    axios.delete(`http://localhost:8080/api/attendees/${id}`)
      .then(() => {
        setAttendees(attendees.filter(attendee => attendee.id !== id));
      })
      .catch(error => console.error('Error removing attendee:', error));
  };

  return (
    <div className="card">
      <h1>Attendee Management</h1>

      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Attendee Name"
          value={attendeeData.name}
          onChange={(e) => setAttendeeData({ ...attendeeData, name: e.target.value })}
          required
        />
        <input
          type="email"
          placeholder="Attendee Email"
          value={attendeeData.email}
          onChange={(e) => setAttendeeData({ ...attendeeData, email: e.target.value })}
          required
        />
        <select
          value={attendeeData.eventId}
          onChange={(e) => setAttendeeData({ ...attendeeData, eventId: e.target.value })}
          required
        >
          <option value="">Select Event</option>
          {events.map((event) => (
            <option key={event.id} value={event.id}>{event.name}</option>
          ))}
        </select>
        <button type="submit">Add Attendee</button>
      </form>

      <h2>Attendee List</h2>
      <table>
        <thead>
          <tr>
            <th>Name</th>
            <th>Email</th>
            <th>Assigned Event</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {attendees.map((attendee) => (
            <tr key={attendee.id}>
              <td>{attendee.name}</td>
              <td>{attendee.email}</td>
              <td>{attendee.eventName ? attendee.eventName : 'No Event'}</td>

              <td>
                <button onClick={() => handleRemove(attendee.id)}>Remove</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default AttendeeManagementPage;
