import React from 'react';
import './../assets/styles.css'; // Ensure your styles are here or in a new CSS file
import './../assets/task.css'; // You may need to create this file for task-specific styles
import editIcon from './../assets/edit.gif'; // Add your path here
import deleteIcon from './../assets/bin.gif'; // Add your path here

const TaskBox = ({ tasks, onEdit, handleRemove }) => {
  return (
    <div className="task-list">
      {tasks.map((task) => (
        <div className="task-box" key={task.id}>
          <h3><strong>Task:</strong> {task.name}</h3>
          <p><strong>Deadline:</strong> {task.deadline}</p>
          <p><strong>Status:</strong> {task.status}</p>
          <div className="task-actions">
            <img
              src={editIcon}
              alt="Edit"
              className="action-icon"
              onClick={() => onEdit(task)}
            />
            <img
              src={deleteIcon}
              alt="Delete"
              className="action-icon"
              onClick={() => handleRemove(task.id)}
            />
          </div>
        </div>
      ))}
    </div>
  );
};

export default TaskBox;
