import React, { useEffect, useState } from "react";
import { getAllEvents, createEvent } from "../services/Api";

const EventManagement = () => {
  const [events, setEvents] = useState([]); // Initialize as empty array
  const [newEvent, setNewEvent] = useState({ name: "", description: "", location: "", date: "" });

  useEffect(() => {
    const fetchEvents = async () => {
      try {
        const data = await getAllEvents();
        console.log("Fetched Events:", data); // Log the fetched data to debug

        // Clean up the data (remove repetitive nested tasks)
        const cleanedData = data.map(event => ({
          ...event,
          tasks: event.tasks && Array.isArray(event.tasks) ? event.tasks.slice(0, 1) : [] // Assuming you want only the first task
        }));

        setEvents(cleanedData); // Update state with cleaned data
      } catch (error) {
        console.error('Error fetching events:', error); // Log any error while fetching data
      }
    };
    fetchEvents();
  }, []); // Run once when the component mounts

  const handleCreateEvent = async (e) => {
    e.preventDefault();
    try {
      const createdEvent = await createEvent(newEvent); // Create new event
      if (createdEvent) {
        setEvents((prevEvents) => [...prevEvents, createdEvent]); // Append the new event to existing ones
        setNewEvent({ name: "", description: "", location: "", date: "" }); // Reset form
      }
    } catch (error) {
      console.error('Error creating event:', error); // Log any error while creating event
    }
  };

  return (
    <div>
      <h2>Event Management</h2>
      <form onSubmit={handleCreateEvent}>
        <input
          type="text"
          placeholder="Event Name"
          value={newEvent.name}
          onChange={(e) => setNewEvent({ ...newEvent, name: e.target.value })}
        />
        <input
          type="text"
          placeholder="Description"
          value={newEvent.description}
          onChange={(e) => setNewEvent({ ...newEvent, description: e.target.value })}
        />
        <input
          type="text"
          placeholder="Location"
          value={newEvent.location}
          onChange={(e) => setNewEvent({ ...newEvent, location: e.target.value })}
        />
        <input
          type="date"
          value={newEvent.date}
          onChange={(e) => setNewEvent({ ...newEvent, date: e.target.value })}
        />
        <button type="submit">Create Event</button>
      </form>

      <h3>Event List</h3>
      <ul>
        {Array.isArray(events) && events.length > 0 ? (
          events.map((event) => (
            <li key={event.id}>
              {event.name} - {event.description} - {event.location} - {event.date}
            </li>
          ))
        ) : (
          <p>No events found</p>
        )}
      </ul>
    </div>
  );
};

export default EventManagement;
