import React, { useState, useEffect } from 'react';
import axios from 'axios';
import AttendeeForm from './AttendeeForm';
import AttendeeBox from './AttendeeBox';
import "./../assets/styles.css";
import "./../assets/attendee.css";

const AttendeeManagementPage = () => {
  const [attendees, setAttendees] = useState([]);
  const [events, setEvents] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [isEditing, setIsEditing] = useState(false);
  const [initialFormData, setInitialFormData] = useState({ id: '', name: '', email: '', eventId: '' });

  useEffect(() => {
    axios.get('http://localhost:8080/api/attendees')
      .then(response => setAttendees(response.data))
      .catch(error => console.error('Error fetching attendees:', error));

    axios.get('http://localhost:8080/api/events')
      .then(response => setEvents(response.data))
      .catch(error => console.error('Error fetching events:', error));
  }, []);

  const handleFormSubmit = (formData) => {
    const method = isEditing ? 'put' : 'post';
    const url = isEditing ? `http://localhost:8080/api/attendees/${formData.id}` : 'http://localhost:8080/api/attendees';

    axios({ method, url, data: formData })
      .then(response => {
        setAttendees(prevAttendees => {
          if (isEditing) {
            return prevAttendees.map(attendee => (attendee.id === response.data.id ? response.data : attendee));
          }
          return [...prevAttendees, response.data];
        });
        setShowForm(false);
        setIsEditing(false);
      })
      .catch(error => console.error('Error creating/updating attendee:', error));
  };

  const handleEdit = (attendee) => {
    setInitialFormData(attendee);
    setIsEditing(true);
    setShowForm(true);
  };

  const handleCancel = () => {
    setShowForm(false);
    setIsEditing(false);
    setInitialFormData({ id: '', name: '', email: '', eventId: '' });
  };

  const handleRemove = (id) => {
    axios.delete(`http://localhost:8080/api/attendees/${id}`)
      .then(() => setAttendees(attendees.filter(attendee => attendee.id !== id)))
      .catch(error => console.error('Error removing attendee:', error));
  };

  return (
    <div className="card">
      <div className="header">
        <h1 className="attendee-title">Attendees</h1>
      </div>
      <div className="add-attendee-btn-container">
        <button onClick={() => setShowForm(true)} className="add-attendee-btn">+ Add Attendee</button>
      </div>
      {!showForm ? (
        <>
          <div className="attendee-list">
            {attendees.map((attendee) => (
            <AttendeeBox
              key={attendee.id}
              attendee={attendee}
              onEdit={handleEdit}
              handleRemove={handleRemove}
            />
          ))}
        </div>
        </>
      ) : (
        <AttendeeForm
          initialData={initialFormData}
          setAttendeeData={setInitialFormData}
          events={events}
          onSubmit={handleFormSubmit}
          onCancel={handleCancel}
        />
      )}
    </div>
  );
};

export default AttendeeManagementPage;
