import React, { useState, useEffect } from 'react';
import axios from 'axios';
import "./../assets/styles.css";
const EventManagementPage = () => {
  const [events, setEvents] = useState([]);
  const [isEditing, setIsEditing] = useState(false);
  const [eventData, setEventData] = useState({ id: '', name: '', description: '', location: '', date: '' });

  // Fetch the list of events from backend
  useEffect(() => {
    axios.get('http://localhost:8080/api/events')
      .then(response => {
        setEvents(response.data);
      })
      .catch(error => console.error('Error fetching events:', error));
  }, []);

  // Add or update event
  const handleSubmit = (e) => {
    e.preventDefault();

    const method = isEditing ? 'put' : 'post';
    const url = isEditing ? `http://localhost:8080/api/events/${eventData.id}` : 'http://localhost:8080/api/events';

    axios({
      method,
      url,
      data: eventData,
    })
      .then(response => {
        setEvents(prevEvents => {
          if (isEditing) {
            return prevEvents.map(event =>
              event.id === response.data.id ? response.data : event
            );
          }
          return [...prevEvents, response.data];
        });
        setIsEditing(false);
        setEventData({ id: '', name: '', description: '', location: '', date: '' });
      })
      .catch(error => console.error('Error creating/updating event:', error));
  };

  // Delete event
  const handleDelete = (id) => {
    axios.delete(`http://localhost:8080/api/events/${id}`)
      .then(() => {
        setEvents(events.filter(event => event.id !== id));
      })
      .catch(error => console.error('Error deleting event:', error));
  };

  // Set data to edit event
  const handleEdit = (event) => {
    setEventData(event);
    setIsEditing(true);
  };

  return (
    <div className="card">
      <h1>Event Management</h1>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Event Name"
          value={eventData.name}
          onChange={(e) => setEventData({ ...eventData, name: e.target.value })}
          required
        />
        <input
          type="text"
          placeholder="Description"
          value={eventData.description}
          onChange={(e) => setEventData({ ...eventData, description: e.target.value })}
          required
        />
        <input
          type="text"
          placeholder="Location"
          value={eventData.location}
          onChange={(e) => setEventData({ ...eventData, location: e.target.value })}
          required
        />
        <input
          type="date"
          value={eventData.date}
          onChange={(e) => setEventData({ ...eventData, date: e.target.value })}
          required
        />
        <button type="submit">{isEditing ? 'Update Event' : 'Add Event'}</button>
      </form>

      <h2>Event List</h2>
      <table>
        <thead>
          <tr>
            <th>Name</th>
            <th>Description</th>
            <th>Location</th>
            <th>Date</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {events.map((event) => (
            <tr key={event.id}>
              <td>{event.name}</td>
              <td>{event.description}</td>
              <td>{event.location}</td>
              <td>{event.date}</td>
              <td>
                <button onClick={() => handleEdit(event)}>Edit</button>
                <button onClick={() => handleDelete(event.id)}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default EventManagementPage;
