import React, { useState, useEffect } from 'react';
import axios from 'axios';

const TaskTrackerPage = () => {
  const [tasks, setTasks] = useState([]);
  const [events, setEvents] = useState([]);
  const [selectedEvent, setSelectedEvent] = useState('');
  const [taskData, setTaskData] = useState({ name: '', deadline: '', eventId: '', status: 'Pending' });

  // Fetch tasks and events
  useEffect(() => {
    axios.get('http://localhost:8080/api/events')
      .then(response => setEvents(response.data))
      .catch(error => console.error('Error fetching events:', error));

    if (selectedEvent) {
      axios.get(`http://localhost:8080/api/tasks/event/${selectedEvent}`)
        .then(response => setTasks(response.data))
        .catch(error => console.error('Error fetching tasks:', error));
    }
  }, [selectedEvent]);

  // Add a new task
  const handleAddTask = (e) => {
    e.preventDefault();
    if (!taskData.eventId) {
      alert("Please select an event.");
      return;
    }

    axios.post('http://localhost:8080/api/tasks', {
      name: taskData.name,
      deadline: taskData.deadline,
      eventId: taskData.eventId,
      status: taskData.status
    })
      .then(response => {
        setTasks([...tasks, response.data]);
        setTaskData({ name: '', deadline: '', eventId: '', status: 'Pending' });
      })
      .catch(error => console.error('Error adding task:', error));
  };

  // Update task status
  const handleUpdateStatus = (taskId, status) => {
    axios.put(`http://localhost:8080/api/tasks/${taskId}`, { status })
      .then(response => {
        setTasks(tasks.map(task => task.id === taskId ? { ...task, status } : task));
      })
      .catch(error => console.error('Error updating task:', error));
  };

  return (
    <div>
      <h1>Task Tracker</h1>
      <select onChange={e => setSelectedEvent(e.target.value)} value={selectedEvent}>
        <option value="">Select Event</option>
        {events.map(event => (
          <option key={event.id} value={event.id}>{event.name}</option>
        ))}
      </select>
      <form onSubmit={handleAddTask}>
        <input
          type="text"
          placeholder="Task Name"
          value={taskData.name}
          onChange={(e) => setTaskData({ ...taskData, name: e.target.value })}
        />
        <input
          type="date"
          value={taskData.deadline}
          onChange={(e) => setTaskData({ ...taskData, deadline: e.target.value })}
        />
        <select
          value={taskData.eventId}
          onChange={(e) => setTaskData({ ...taskData, eventId: e.target.value })}
        >
          <option value="">Select Event</option>
          {events.map(event => (
            <option key={event.id} value={event.id}>{event.name}</option>
          ))}
        </select>
        <button type="submit">Add Task</button>
      </form>
      <ul>
        {tasks.map(task => (
          <li key={task.id}>
            {task.name} - {task.deadline} - {task.status}
            <button onClick={() => handleUpdateStatus(task.id, 'Completed')}>Complete</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default TaskTrackerPage;
