import React from 'react';
import "./../assets/styles.css"; // Ensure you style your boxes here or in a new CSS file
import editIcon from './../assets/edit-icon.png'; // Add your path here
import deleteIcon from './path/to/delete-icon.png'; // Add your path here

const EventBox = ({ event, onEdit, onDelete }) => {
  return (
    <div className="event-box">
      <h3>{event.name}</h3>
      <p>{event.description}</p>
      <p><strong>Location:</strong> {event.location}</p>
      <p><strong>Date:</strong> {event.date}</p>
      <div className="event-actions">
      <img
        src={editIcon}
        alt="Edit"
        className="action-icon"
        onClick={() => handleEdit(event)}
       />
       <img
        src={deleteIcon}
        alt="Delete"
                className="action-icon"
                onClick={() => handleDelete(event.id)}
              />
      </div>
    </div>
  );
};

export default EventBox;
