import React, { useState, useEffect } from "react";
import { getAllAttendees, createAttendee } from "../services/Api";

const AttendeeManagement = () => {
  const [attendees, setAttendees] = useState([]);
  const [newAttendee, setNewAttendee] = useState({ name: "", email: "", event: "" });

  useEffect(() => {
    const fetchAttendees = async () => {
      const data = await getAllAttendees();
      setAttendees(data);
    };
    fetchAttendees();
  }, []);

  const handleCreateAttendee = async (e) => {
    e.preventDefault();
    const createdAttendee = await createAttendee(newAttendee);
    setAttendees([...attendees, createdAttendee]);
    setNewAttendee({ name: "", email: "", event: "" });
  };

  return (
    <div>
      <h2>Attendee Management</h2>
      <form onSubmit={handleCreateAttendee}>
        <input
          type="text"
          placeholder="Attendee Name"
          value={newAttendee.name}
          onChange={(e) => setNewAttendee({ ...newAttendee, name: e.target.value })}
        />
        <input
          type="email"
          placeholder="Attendee Email"
          value={newAttendee.email}
          onChange={(e) => setNewAttendee({ ...newAttendee, email: e.target.value })}
        />
        <input
          type="text"
          placeholder="Event"
          value={newAttendee.event}
          onChange={(e) => setNewAttendee({ ...newAttendee, event: e.target.value })}
        />
        <button type="submit">Create Attendee</button>
      </form>

      <h3>Attendee List</h3>
      <ul>
        {attendees.map((attendee) => (
          <li key={attendee.id}>
            {attendee.name} - {attendee.email} - {attendee.event}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default AttendeeManagement;
