import React, { useEffect, useState } from "react";
import { getAllEvents } from "../services/Api"; // assuming this function fetches events

const EventManagement = () => {
  const [events, setEvents] = useState([]); // Default state as an array
  const [newEvent, setNewEvent] = useState({ title: "", description: "" });

  useEffect(() => {
    // Fetching events from API
    const fetchEvents = async () => {
      const data = await getAllEvents();
      if (Array.isArray(data)) {
        setEvents(data); // Update events if the data is valid
      } else {
        console.error('Events data is not an array:', data); // Log error if it's not an array
      }
    };
    fetchEvents(); // Call fetch on mount
  }, []);

  const handleCreateEvent = async (e) => {
    e.preventDefault();
    // Create new event logic
    const createdEvent = await createEvent(newEvent); // Assuming you have a createEvent function
    setEvents([...events, createdEvent]); // Add new event to the list
    setNewEvent({ title: "", description: "" }); // Reset form
  };

  return (
    <div>
      <h2>Event Management</h2>
      <form onSubmit={handleCreateEvent}>
        <input
          type="text"
          placeholder="Event Title"
          value={newEvent.title}
          onChange={(e) => setNewEvent({ ...newEvent, title: e.target.value })}
        />
        <input
          type="text"
          placeholder="Event Description"
          value={newEvent.description}
          onChange={(e) => setNewEvent({ ...newEvent, description: e.target.value })}
        />
        <button type="submit">Create Event</button>
      </form>

      <h3>Event List</h3>
      <ul>
        {events.length > 0 ? (
          events.map((event) => (
            <li key={event.id}>
              {event.title} - {event.description}
            </li>
          ))
        ) : (
          <p>No events found</p>
        )}
      </ul>
    </div>
  );
};

export default EventManagement;
