import React, { useState, useEffect } from 'react';
import axios from 'axios';

const TaskTrackerPage = () => {
  const [tasks, setTasks] = useState([]);
  const [events, setEvents] = useState([]);
  const [selectedEvent, setSelectedEvent] = useState('');
  const [taskData, setTaskData] = useState({ name: '', deadline: '', eventId: '', status: 'Pending' });

  // Fetch tasks and events when the component mounts or when selectedEvent changes
  useEffect(() => {
    // Fetch events
    axios.get('http://localhost:8080/api/events')
      .then(response => setEvents(response.data))
      .catch(error => console.error('Error fetching events:', error));

    // Fetch tasks based on selected event, if any
    if (selectedEvent) {
      axios.get(`http://localhost:8080/api/tasks/event/${selectedEvent}`)
        .then(response => setTasks(response.data))
        .catch(error => console.error('Error fetching tasks:', error));
    } else {
      // Fetch all tasks if no event is selected
      axios.get('http://localhost:8080/api/tasks')
        .then(response => setTasks(response.data))
        .catch(error => console.error('Error fetching tasks:', error));
    }
  }, [selectedEvent]); // This will run whenever selectedEvent changes

  // Add a new task
  const handleAddTask = (e) => {
    e.preventDefault();
    const formattedDeadline = new Date(taskData.deadline).toISOString().split('T')[0]; // yyyy-MM-dd format
    const taskToCreate = { ...taskData, deadline: formattedDeadline };

    axios.post('http://localhost:8080/api/tasks', taskToCreate)
      .then(response => {
        setTasks([...tasks, response.data]);
        setTaskData({ name: '', deadline: '', eventId: '', status: 'Pending' });
      })
      .catch(error => console.error('Error adding task:', error));
  };

  // Update task status
  const handleUpdateStatus = (taskId, status) => {
    axios.put(`http://localhost:8080/api/tasks/${taskId}`, { status })
      .then(response => {
        setTasks(tasks.map(task => task.id === taskId ? { ...task, status } : task));
      })
      .catch(error => console.error('Error updating task:', error));
  };

  return (
    <div>
      <h1>Task Tracker</h1>

      <div>
        <label>Select Event:</label>
        <select
          value={selectedEvent}
          onChange={e => setSelectedEvent(e.target.value)}
        >
          <option value="">--Select Event--</option>
          {events.map(event => (
            <option key={event.id} value={event.id}>
              {event.name}
            </option>
          ))}
        </select>
      </div>

      <form onSubmit={handleAddTask}>
        <div>
          <label>Task Name:</label>
          <input
            type="text"
            value={taskData.name}
            onChange={(e) => setTaskData({ ...taskData, name: e.target.value })}
            required
          />
        </div>

        <div>
          <label>Task Deadline:</label>
          <input
            type="date"
            value={taskData.deadline}
            onChange={(e) => setTaskData({ ...taskData, deadline: e.target.value })}
            required
          />
        </div>

        <div>
          <label>Status:</label>
          <select
            value={taskData.status}
            onChange={(e) => setTaskData({ ...taskData, status: e.target.value })}
          >
            <option value="Pending">Pending</option>
            <option value="In Progress">In Progress</option>
            <option value="Completed">Completed</option>
          </select>
        </div>

        <div>
          <label>Event:</label>
          <select
            value={taskData.eventId}
            onChange={(e) => setTaskData({ ...taskData, eventId: e.target.value })}
            required
          >
            <option value="">--Select Event--</option>
            {events.map(event => (
              <option key={event.id} value={event.id}>
                {event.name}
              </option>
            ))}
          </select>
        </div>

        <button type="submit">Add Task</button>
      </form>

      <div>
        <h2>Task List</h2>
        <ul>
          {tasks.map((task) => (
            <li key={task.id}>
              <span>{task.name}</span> - <span>{task.deadline}</span> - <span>{task.status}</span>
              <button onClick={() => handleUpdateStatus(task.id, 'Completed')}>Mark as Completed</button>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
};

export default TaskTrackerPage;
