import React, { useEffect, useState } from "react";
import { getAllEvents, createEvent } from "../services/Api";

const EventManagement = () => {
  const [events, setEvents] = useState([]);
  const [newEvent, setNewEvent] = useState({ name: "", description: "", location: "", date: "" });

  useEffect(() => {
    const fetchEvents = async () => {
      const data = await getAllEvents();
      setEvents(data);
    };
    fetchEvents();
  }, []);

  const handleCreateEvent = async (e) => {
    e.preventDefault();
    const createdEvent = await createEvent(newEvent);
    setEvents([...events, createdEvent]);
    setNewEvent({ name: "", description: "", location: "", date: "" });
  };

  return (
    <div>
      <h2>Event Management</h2>
      <form onSubmit={handleCreateEvent}>
        <input
          type="text"
          placeholder="Event Name"
          value={newEvent.name}
          onChange={(e) => setNewEvent({ ...newEvent, name: e.target.value })}
        />
        <input
          type="text"
          placeholder="Description"
          value={newEvent.description}
          onChange={(e) => setNewEvent({ ...newEvent, description: e.target.value })}
        />
        <input
          type="text"
          placeholder="Location"
          value={newEvent.location}
          onChange={(e) => setNewEvent({ ...newEvent, location: e.target.value })}
        />
        <input
          type="date"
          value={newEvent.date}
          onChange={(e) => setNewEvent({ ...newEvent, date: e.target.value })}
        />
        <button type="submit">Create Event</button>
      </form>

      <h3>Event List</h3>
      <ul>
        {events.map((event) => (
          <li key={event.id}>
            {event.name} - {event.description} - {event.location} - {event.date}
          </li>
        ))}
      </ul>
    </div>
  );
};

export default EventManagement;
