import React from 'react';

const TaskBox = ({ tasks, handleUpdateStatus }) => {
  return (
    <div className="task-box">
      <h2>Task List</h2>
      <table>
        <thead>
          <tr>
            <th>Name</th>
            <th>Deadline</th>
            <th>Status</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {tasks.map((task) => (
            <tr key={task.id}>
              <td>{task.name}</td>
              <td>{task.deadline}</td>
              <td>{task.status}</td>
              <td>
                <button onClick={() => handleUpdateStatus(task.id, 'Completed')}>
                  Mark as Completed
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default TaskBox;
