// TaskTrackerPage.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import TaskBox from './TaskBox'; // Import the TaskBox component
import TaskForm from './TaskForm'; // Import the TaskForm component
import "./../assets/styles.css";

const TaskTrackerPage = () => {
  const [tasks, setTasks] = useState([]);
  const [events, setEvents] = useState([]);
  const [selectedEvent, setSelectedEvent] = useState('');

  // Fetch tasks and events when the component mounts or when selectedEvent changes
  useEffect(() => {
    // Fetch events
    axios.get('http://localhost:8080/api/events')
      .then(response => setEvents(response.data))
      .catch(error => console.error('Error fetching events:', error));

    // Fetch tasks based on selected event, if any
    if (selectedEvent) {
      axios.get(`http://localhost:8080/api/tasks/event/${selectedEvent}`)
        .then(response => setTasks(response.data))
        .catch(error => console.error('Error fetching tasks:', error));
    } else {
      // Fetch all tasks if no event is selected
      axios.get('http://localhost:8080/api/tasks')
        .then(response => setTasks(response.data))
        .catch(error => console.error('Error fetching tasks:', error));
    }
  }, [selectedEvent]);

  // Add a new task
  const handleAddTask = (taskData) => {
    const formattedDeadline = new Date(taskData.deadline).toISOString().split('T')[0]; // yyyy-MM-dd format
    const taskToCreate = { ...taskData, deadline: formattedDeadline };

    axios.post('http://localhost:8080/api/tasks', taskToCreate)
      .then(response => {
        setTasks([...tasks, response.data]);
      })
      .catch(error => console.error('Error adding task:', error));
  };

  // Update task status
  const handleUpdateStatus = (taskId, status) => {
    axios.put(`http://localhost:8080/api/tasks/${taskId}`, { status })
      .then(response => {
        setTasks(tasks.map(task => 
          task.id === taskId ? { ...task, status } : task
        ));
      })
      .catch(error => console.error('Error updating task:', error));
  };

  return (
    <div className="card">
      <h1>Task Tracker</h1>
      
      <TaskForm events={events} onSubmit={handleAddTask} />

      <div>
        <h2>Task List</h2>
        <div className="task-list">
          {tasks.map((task) => (
            <TaskBox
              key={task.id}
              task={task}
              onUpdateStatus={handleUpdateStatus}
            />
          ))}
        </div>
      </div>
    </div>
  );
};

export default TaskTrackerPage;
