import React, { useState, useEffect } from 'react';
import axios from 'axios';
import TaskForm from './TaskForm';
import TaskBox from './TaskBox';
import "./../assets/styles.css";

const TaskTrackerPage = () => {
  const [tasks, setTasks] = useState([]);
  const [events, setEvents] = useState([]);
  const [showForm, setShowForm] = useState(false);
  const [isEditing, setIsEditing] = useState(false);
  const [taskData, setTaskData] = useState({ name: '', deadline: '', eventId: '', status: 'Pending' });

  useEffect(() => {
    axios.get('http://localhost:8080/api/events')
      .then(response => setEvents(response.data))
      .catch(error => console.error('Error fetching events:', error));

    const fetchTasks = selectedEvent
      ? axios.get(`http://localhost:8080/api/tasks/event/${selectedEvent}`)
      : axios.get('http://localhost:8080/api/tasks');

    fetchTasks
      .then(response => setTasks(response.data))
      .catch(error => console.error('Error fetching tasks:', error));
  }, []);

  const handleSubmit = (e) => {
    e.preventDefault();
    const formattedDeadline = new Date(taskData.deadline).toISOString().split('T')[0];
    const taskToCreateOrUpdate = { ...taskData, deadline: formattedDeadline };

    const method = isEditing ? 'put' : 'post';
    const url = isEditing
      ? `http://localhost:8080/api/tasks/${taskData.id}`
      : 'http://localhost:8080/api/tasks';

    axios({ method, url, data: taskToCreateOrUpdate })
      .then(response => {
        setTasks(prevTasks => {
          if (isEditing) {
            return prevTasks.map(task => (task.id === response.data.id ? response.data : task));
          }
          return [...prevTasks, response.data];
        });
        resetForm();
      })
      .catch(error => console.error('Error saving task:', error));
  };

  const handleEdit = (task) => {
    setTaskData(task);
    setIsEditing(true);
    setShowForm(true);
  };

  const handleCancel = () => {
    resetForm();
  };

  const handleRemove = (id) => {
    axios.delete(`http://localhost:8080/api/tasks/${id}`)
      .then(() => setTasks(tasks.filter(task => task.id !== id)))
      .catch(error => console.error('Error removing task:', error));
  };

  const resetForm = () => {
    setTaskData({ name: '', deadline: '', eventId: '', status: 'Pending' });
    setIsEditing(false);
    setShowForm(false);
  };

  const handleUpdateStatus = (taskId, status) => {
    axios.put(`http://localhost:8080/api/tasks/${taskId}`, { status })
      .then(() => {
        setTasks(tasks.map(task =>
          task.id === taskId ? { ...task, status } : task
        ));
      })
      .catch(error => console.error('Error updating task:', error));
  };

  return (
    <div className="card">
      <div className="header">
        <h1>Task Tracker</h1>
      </div>
      <div className="add-task-btn-container">
        <button onClick={() => setShowForm(true)} className="add-task-btn">+ Add Task</button>
      </div>
      {showForm ? (
        <TaskForm
          taskData={taskData}
          setTaskData={setTaskData}
          events={events}
          handleSubmit={handleSubmit}
          onCancel={handleCancel}
        />
      ) : (
        <TaskBox tasks={tasks} onEdit={handleEdit} handleRemove={handleRemove} handleUpdateStatus={handleUpdateStatus} />
      )}
    </div>
  );
};

export default TaskTrackerPage;
